import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Reports.css"; // Create this file if you want custom styles

const BinComponent = () => {
  const [reports, setReports] = useState([]);

  useEffect(() => {
    const fetchReports = async () => {
      try {
        const response = await fetch("http://localhost:1337/api/reports"); // your actual endpoint
        if (response.ok) {
          const data = await response.json();
          setReports(data.reports || []);
        } else {
          console.error("Failed to fetch reports:", response.statusText);
        }
      } catch (error) {
        console.error("Error fetching reports:", error);
      }
    };

    fetchReports();
  }, []);

  return (
    <div className="container py-4">
      <h2 className="mb-4">Citizen Waste Reports</h2>
      <div className="row">
        {reports.length === 0 ? (
          <p>No reports submitted yet.</p>
        ) : (
          reports.map((report) => (
            <div key={report._id} className="col-md-6 col-lg-4 mb-4">
              <div className="card h-100 shadow-sm">
                {report.photo && (
                  <img
                    src={report.photo}
                    className="card-img-top"
                    alt="Waste Report"
                    style={{ height: "200px", objectFit: "cover" }}
                  />
                )}
                <div className="card-body">
                  <h5 className="card-title text-capitalize">
                    {report.type === "dump" ? "Illegal Dump Site" : "Public Bin"}
                  </h5>
                  <p className="card-text">{report.description}</p>
                  <p className="text-muted small">
                    üìç {report.latitude}, {report.longitude}
                    <br />
                    üïí {new Date(report.createdAt).toLocaleString()}
                  </p>
                  <span
                    className={`badge ${
                      report.status === "Collected" ? "bg-success" : "bg-warning text-dark"
                    }`}
                  >
                    {report.status}
                  </span>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default BinComponent;
