import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const BinComponent = () => {
  const [reports, setReports] = useState([]);

  useEffect(() => {
    const fetchReports = async () => {
      try {
        const response = await fetch("http://localhost:1337/api/reports"); // Change this endpoint when backend is ready
        if (response.ok) {
          const data = await response.json();
          setReports(data.reports || []); // adjust based on backend structure
        } else {
          console.error("Failed to fetch reports:", response.statusText);
        }
      } catch (error) {
        console.error("Error fetching reports:", error);
      }
    };

    fetchReports();
  }, []);

  return (
    <div className="container">
      <h2 className="my-4">Citizen Waste Reports</h2>
      <p>Below are the waste reports submitted by residents.</p>

      {reports.length === 0 ? (
        <p>No reports available yet.</p>
      ) : (
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Report ID</th>
              <th>Description</th>
              <th>Location</th>
              <th>Date Submitted</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {reports.map((report) => (
              <tr key={report._id}>
                <td>{report._id}</td>
                <td>{report.description}</td>
                <td>
                  {report.latitude}, {report.longitude}
                </td>
                <td>{new Date(report.createdAt).toLocaleDateString()}</td>
                <td>{report.status || "Pending"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default BinComponent;
